    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title 'Les formations de tendence' %}


{% block body %}
    <div class=\"container-fluid\" style =\"margin-top:90px; padding:0\">
        <div class=\"jumbotron\" style=\"padding: 60px\">
            <div class=\"container\">
                {{ form_start(form) }}
                <div class=\"form-row\">
                    <div class=\"col-md-5\">
                        {{ form_row(form.maxNote) }}
                    </div>
                    <div class=\"col-md-5\">
                        {{ form_row(form.maxLevel) }}
                    </div>
                    <div class=\"col\">
                        <button class=\"btn btn-warning\" style=\"background: #337ab7;border-color:#337ab7\"> Rechercher</button>
                    </div>
                </div>
                {{ form_end(form) }}
            </div>
        </div>
    </div>
    <div class=\"container\">
        <div class=\"sous-titre\" style=\"width: 60%;margin-bottom: 30px\">
            <h2 style=\"font-size: 50px\">Toutes les formations </h2>
        </div>
        <div class=\"row\" style=\"background-color: #f7f7f7;\">
            {% for formation in formations %}
                <div class=\"col-sm-4 col-md-3\">
                    {{ include ('pages/CommonTemplates/_afficheFormations.html.twig') }}
                </div>
            {% endfor %}
        </div>
        <div class=\"pagination container\">
            {{ knp_pagination_render(formations) }}
        </div>
    </div>


{% endblock %}", "pages/formation.html.twig", "/Users/alessandro/Desktop/version2/templates/pages/formation.html.twig");
